import { Pie } from '@antv/g2plot';

const data = [
  { type: '分类一', value: 27 },
  { type: '分类二', value: 25 },
  { type: '分类三', value: 18 },
  { type: '分类四', value: 15 },
  { type: '分类五', value: 10 },
  { type: '其他', value: 5 },
];

const pattern = ({ type }) => {
  if (type === '分类一') {
    return {
      type: 'line',
      cfg: {
        lineWidth: 6,
        spacing: 5,
      },
    };
  } else if (type === '分类三') {
    return {
      type: 'dot',
      cfg: {
        size: 10,
      },
    };
  }
  return;
};

