  canvas.style.width = `${width}px`;
  canvas.style.height = `${height}px`;

  const context = canvas.getContext('2d');
  context.scale(pixelRatio, pixelRatio);

  // 2. 绘制
  context.globalAlpha = 1;
  context.lineWidth = 0;

  const padding = 4;

  drawRectangle(context, 0, 0, width, height, color);

