      };
    }
    return {
      fill: 'p(a)https://gw.alipayobjects.com/zos/antfincdn/Ye2DqRx%2627/pie-wenli2.png',
    };
  },
  tooltip: false,
  interactions: [{ type: 'element-single-selected' }],
});

piePlot.render();

