  tooltip: {
    formatter: (datum) => {
      return { name: 'star数', value: datum.star };
    },
  },
  colorField: 'star',
  color: ({ star }) => {
    if (star > 10000) {
      return '#36c361';
    } else if (star > 1000) {
      return '#2194ff';
    }
    return '#ff4d4f';
  },
});
bar.render();

