import { Pie, G2 } from '@antv/g2plot';

const G = G2.getEngine('canvas');

const data = [
  { sex: '男', sold: 0.45 },
  { sex: '女', sold: 0.55 },
];

const piePlot = new Pie('container', {
  appendPadding: 10,
  data,
  angleField: 'sold',
  colorField: 'sex',
  radius: 0.66,
  color: ['#1890ff', '#f04864'],
  label: {
    content: (obj) => {
      const group = new G.Group({});
      group.addShape({
        type: 'image',
        attrs: {
          x: 0,
          y: 0,
          width: 40,
          height: 50,
          img:
            obj.sex === '男'
              ? 'https://gw.alipayobjects.com/zos/rmsportal/oeCxrAewtedMBYOETCln.png'
              : 'https://gw.alipayobjects.com/zos/rmsportal/mweUsJpBWucJRixSfWVP.png',
        },
      });

      group.addShape({
        type: 'text',
        attrs: {
          x: 20,
          y: 54,
          text: obj.sex,
          textAlign: 'center',
          textBaseline: 'top',
          fill: obj.sex === '男' ? '#1890ff' : '#f04864',
        },
      });
      return group;
    },
  },
  interactions: [{ type: 'element-active' }],
});

piePlot.render();

